import styled from 'styled-components';

export const Container = styled.div`
    position: relative;
    overflow: hidden; 
    width: 400px;
    height: 400px;
    cursor: pointer;
    :hover div, div div{
        transform: scale(1.2);
        opacity: 1;
    }
`;

export const Background = styled.div`
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: center;
    transform: scale(1);
    transition: all .5s;

`;

export const Info = styled.div`
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    background: rgba(0, 0, 0, 0.5);
    opacity: 0;
    transition: all .8s;
    display: flex;
    justify-content: center;
    align-items: center;
    ul {
        li {
            color: #FFF;
            font-family: "Calibri Regular";
            font-size: 20px;
            margin-top: 10px;
        }
    }

`;
